var Sort=function(){var A=this;var B=[];A.getData=function(){return B.concat([])};A.asc=function(C){if(typeof(C)==="string"&&C.length>0){B.push({Field:C,Sort:1});return A}throw {message:"排序键不可为空",trace:console.trace()}};A.desc=function(C){if(typeof(C)==="string"&&C.length>0){B.push({Field:C,Sort:-1});return A}throw {message:"排序键不可为空",trace:console.trace()}};A.reset=function(){B.splice(0);return A};A.revoked=function(){B.pop();return A}};Sort.asc=function(A){return new Sort().asc(A)};Sort.desc=function(A){return new Sort().desc(A)};